INSERT INTO `product_category` (`id`, `title`, `parent_id`, `show_in_menu`, `description`) VALUES
(1, 'کالای دیجیتال', 0, 'Y', 'عمر انقلاب دیجیتال به کمتر از نیم قرن می‌رسد، اما همین برهه‌ی زمانی کوتاه در تاریخ، زندگی نوع بشر را کاملا دگرگون کرده و شتاب قابل توجهی به پیشرفت انسان در زمینه‌های مختلف بخشیده است. شاید اولین قدم‌های دیجیتالی شدن دنیا را بتوان تغییر تراتزیستورهای آنالوگ به دیجیتال دانست که بستر لازم برای کالای دیجیتال نظیر کامپیوتر، تلفن را فراهم کرد. در حال حاضر محصولات دیجیتالی آنقدر توسعه یافته‌اند که می‌توان ردپای آنها را در ساده‌ترین امور روزمره مشاهده کرد. بعضی از این محصولات به ابزارهای جدانشدنی برای انسان تبدیل شده‌اند، تصور زندگی بدون آنها و امکاناتی که در اختیار انسان قرار می‌دهند، وجود ندارد. موبایل‌ها، لپ‌تاپ، ماشین‌های ادارای از این دسته محصولات هستند. کامپیوترهای شخصی، لپ‌تاپ و گوشی تلفن همراه نیز مسیر پر فراز و نشیبی را پشت سر گذاشته‌اند تا به شکل هوشمند امروزی عرضه شود. اولین کامپیوتر قابل حمل جهان در سال 1981 به بازار آمد. این دستگاه صفحه نمایشی 5 اینچی داشت و وزن آن حدود 11 کیلوگرم بود. در حالیکه شرکت‌هایی نظیر اپل یا هواوی لپ‌تاپ‌هایی به وزن یک کیلوگرم ساخته‌اند.'),
(2, 'گوشی موبایل', 1, 'Y', ''),
(3, 'لپ تاپ', 1, 'Y', 'قیمت روز خرید انواع لپ تاپ با برندهای مختلف به همراه لیست فروشندگان، مشخصات و نقد و بررسی تخصصی را در ایمالز مشاهده کنید.'),
(4, 'لوازم جانبی', 1, 'Y', ''),
(5, 'مد و پوشاک', 0, 'Y', 'با گرم شدن تدریجی هوا، کم‌کم باید لباس‌ها و پارچه‌های ضخیم را کنار گذاشته و به سراغ پارچه نازک و خنک برویم. بسیاری از بانوان هنگام خرید، این مشکل را دارند که چه پارچه‌هایی مناسب تابستان است و می‌توانند از آن به عنوان پارچه مانتویی هم استفاده کنند.\r\n\r\nهنگام مشاهده مدل‌های جدید مانتو و ترند سال ۲۰۲۰، انواع گوناگونی از مدل‌ها با پارچه‌های مختلف به چشمتان می‌خورد. این نکته بسیار مهم است، هر مدلی حتماً باید با پارچه منتاسب آن دوخته شود؛ در غیر این صورت جذابیت و زیبایی خود را از دست می‌دهد.'),
(6, 'مردانه', 5, 'Y', ''),
