INSERT INTO `product` (`id`, `title`, `title_en`, `description`, `category_id`, `quantity`, `buy_price`, `sell_price`, `brand_id`, `flags`, `created_by`, `created_at`) VALUES
(1, 'لپ تاپ 15 اینچی ایسوس مدل ZenBook Pro Duo UX581LV', 'ASUS ZenBook Pro Duo UX581LV 15 inch Laptop', '&lt;p style=\\&quot;text-align: right;\\&quot;&gt; نقد و بررسی اجمالی&lt;/p&gt;&lt;h2 style=\\&quot;text-align: right;\\&quot;&gt;ASUS ZenBook Pro Duo UX581LV 15 inch Laptop​&lt;br&gt;&lt;/h2&gt;&lt;p style=\\&quot;text-align: right;\\&quot;&gt;لپ تاپ UX581LV یکی از باریک ترین لپ تاپ های زنبوک شرکت تایوانی ایسوس می باشد که در انتهای سال 2020 رونمایی شد و یکی از قویترین و زیبا ترین لپ تاپ های 15 اینچی حال حاضر بازار می باشد. شرکت ایسوس با استفاده از فلز با کیفیت در ساخت این لپ تاپ ها، باعث گردیده تا در دسته بندی Ultra Book ها همواره رقیبی جدی برای دیگر شرکت های تولید کننده لپ تاپ به شمار روند و همواره مورد استقبال مصرف کنندگان قرار بگیرند.این سری از تولیدات کمپانی ایسوس با مدت زمان نگهداری شارژ زیاد و تعداد درگاه‌های ارتباطی کم و بدون DVD-RW برای رقابت و مقابله با apple macbook طراحی شده است.به طور کلی این دستگاه 15 اینچی با جدید ترین تکنولوژی که وجود 2 نمایشگر مجزا روی محصول می باشد، دارای مزایای بسیاری از جمله نمایشگر های با کیفیت بالا، صفحه‌کلید استاندارد، جزیره‌ای و با نور پس زمینه کیبورد و همچنین بدنه‌ای از جنس فلز بسیار شیک، زیبا و با استحکام بالا است و سیستم سخت‌افزاری آن نیز پاسخگوی همه ی نیازهای طیف وسیعی از کاربران طراح، مدیران، برنامه نویسان، مهندسان، تحلیل گران و … می باشد. ​&lt;br&gt;&lt;/p&gt;', 2, 65, 0, 96000000, 3, 'on', 'دانیال رحیمی', 1600266220),
(2, 'لپ تاپ 15 اینچی ایسوس مدل ROG Strix G531 GV-ZQ', 'ASUS ROG Strix G531GV-ZQ 15 inch Laptop', '&lt;h2&gt;                        نقد و بررسی اجمالی                                                &lt;span&gt;ASUS ROG Strix G531GV-ZQ 15 inch Laptop&lt;/span&gt;&lt;/h2&gt;&lt;div&gt;                                شرکت «ایسوس» در حال گسترش محصولات حرفه‌ای سری STRIX خود است و این سری را با مشخصات انحصاری‌اش در تمامی محصولات خود، از مادربرد و کارت گرافیک گرفته تا لپ‌تاپ، بست داده است. یکی از لپ‌تاپ‌های مخصوص بازی همین سری هم ROG G531G نام دارد. این محصول به‌گونه‌ای طراحی و ساخته شده است که کالایی جذاب و قدرتمند بوده و در عین حال نسبت به رقبای هم‌رده اقتصادی تر باشد. بدنه‌ی این لپ‌تاپ از ترکیب آلومینیوم و پلاستیک ساخته شده است تا وزن آن کمی نسبت به بدنه‌های تمام‌آلومینیومی سبک‌تر شود. قاب پشت صفحه‌نمایش یا همان لایه‌ی رویی لپ‌تاپ از جنس آلومینیوم برس‌خورده‌ی مشکی است که همچنان مانند محصولات قبلی سری G ایسوس، اثرانگشت زیادی به خود جذب می‌کند و باقی بخش‌ها هم (حتی لایه‌ی داخلی فضای اطراف کیبورد) کاملا از پلاستیک ساخته شده‌اند. صفحه‌نمایش15.اینچی این محصول صفحه‌نمایشی با وضوح بالای Full HD یک گزینه‌ی عالی برای فیلم‌دیدن و بازی‌کردن محسوب می‌شود. از دیگر امکانات این مدل می‌توان به کیبورد جزیره‌ای با نور پس‌زمینه، تاچ‌پد یک‌تکه‌ی باکیفیت، پورت‌های متعدد و حرفه‌ای و لوازم جانبی کامل اشاره کرد. این لپ‌تاپ، مخصوص اجرای بازی‌های سنگین روز طراحی و ساخته شده است؛ پس باید سخت‌افزار قوی داشته باشد.&lt;/div&gt;', 3, 1210, 0, 59000000, 3, 'on', 'رسول بسامی', 1600266364),
(3, 'لپ تاپ 15 اینچی اچ پی مدل ProBook 450 G7-G', 'HP ProBook 450 G7-G -15 inch Laptop', '&lt;h2&gt;                        نقد و بررسی اجمالی                                                &lt;span&gt;HP ProBook 450 G7-G -15 inch Laptop&lt;/span&gt;&lt;/h2&gt;&lt;div&gt;                                محصولات سری‌ 450 ‌‌ProBook‌ «اچ‌پی» همیشه انتخاب مناسبی به‌عنوان لپ‌تاپ‌های چندمنظوره در محیط‌های کاری و خانگی بوده‌اند. این دستگاه‌ها کارایی زیاد، امکانات متعدد، بدنه‌ی زیبا و مقاومی دارند. جدیدترین مدل این محصولات‌ «ProBook 450 G7»‌ نام دارد. بدنه‌ی این مدل مانند نسل‌های قبلی تماما از پلاستیک ساخته شده است؛ اما باوجوداین، کیفیت خوب، مقاومت و طول‌عمر زیادی دارد، همچنین در نسل جدید این محصول سبکتر شدن بدنه، طراحی متفاوت و کیبورد بسیار کم عمق و راحت را هم شاهد هستیم. رنگ نقره‌ای پشت آن و تضاد رنگ با کلید های مشکی کیبورد در داخل دستگاه هم ظاهری ساده و درعین‌حال شیک و زیبا به بدنه داده است. ضخامت 1.9 میلی‌متری بدنه‌ی‌ HP 450 G7 از نسل پنجم این سری هم کمتر شده و وزن 2 کیلوگرمی آن، مزیتی به‌شمار می‌رود که جابه‌جایی این دستگاه را آسان می‌کند. صفحه‌نمایش این مدل، کیفیت ‌Full HD با پنل‌ LED Backlight دارد که خوشبختانه به‌دلیل بهره‌بردن از روکش مات، می‌توان به‌راحتی از آن در محیط‌ها و شرایط مختلف استفاده کرد. پورت‌های ورودی و خروجی متعددی روی لبه‌های این لپ‌تاپ تعبیه شده‌اند که برای اتصال انواع ابزارهای جانبی مناسب هستند؛ یک پورت نسل جدید‌ USB type-C ‌هم بین آن‌ها به‌چشم می‌خورد. سخت‌افزار این محصول در بخش‌های پردازنده‌ی مرکزی و پردازشگر گرافیکی به‌روزرسانی شده و در پیکره‌بندی آن از نسل جدید پردازنده‌های‌ سری دهم اینتل و سری ‌MX250 پردازشگرهای‌ NVIDIA ‌استفاده شده است. با توجه به سخت افزار، مدل ‌‌ProBook 450 G7 به‌راحتی می‌تواند برنامه‌های نیمه‌حرفه‌ای و حرفه‌ای را اجرا کند. طبیعتا به‌دلیل نوع کاربری این محصول، نباید اجرای نرم‌افزارهای سنگین سه‌بعدی‌سازی یا تدوین فیلم را از آن توقع داشته باشید. یک باتری سه‌سلولی لیتیوم‌یونی هم وظیفه‌ی توان‌رسانی به این دستگاه را دارد که در شرایط معمول وب‌گردی با اتصال‌ WiFi، حدود پنج ساعت روشن‌ماندن آن را تضمین می‌کند. درمجموع نسل جدید لپ‌تاپ‌های سری ‌ProBook 450 G7 اچ‌پی با بدنه‌ای خوش‌ساخت، صفحه‌نمایشی مات، پورت‌های متعدد و سخت‌افزاری به‌روز، می‌تواند انتخابی مناسب برای کاربری‌های روزانه، نیمه‌حرفه‌ای و حرفه‌ای با قابلیت پاسخ‌گویی به نیاز های به روز کاربر خود باشد.                            &lt;/div&gt;', 3, 50, 0, 3600000, 6, 'on', 'رسول بسامی', 1600266454),
(4, 'لپ تاپ 15 اینچی اچ پی مدل Pavilion CS3457-D', 'HP Pavilion CS3457-D 15 inch Laptop', '&lt;div&gt;                                محصولات سری‌ Pavilion «اچ‌پی» همیشه انتخاب مناسبی به‌عنوان لپ‌تاپ‌های چندمنظوره در محیط‌های کاری و خانگی بوده‌اند. این دستگاه‌ها کارایی زیاد، امکانات متعدد، بدنه‌ی زیبا و مقاومی دارند. جدیدترین مدل این خانواده CS نام دارد که در اصل، ششمین نسل از این سری محسوب می‌شود. بدنه‌ی این مدل مانند نسل‌های قبلی تماما از پلاستیک ساخته شده است؛ اما باوجوداین، کیفیت خوب، مقاومت و طول‌عمر زیادی دارد. رنگ نقره‌ای تیره پشت آن و تضاد رنگ با نقره ای کم رنگ داخل دستگاه هم ظاهری ساده و درعین‌حال شیک و زیبا به بدنه داده است. ضخامت 17 میلی‌متری بدنه‌ی‌ HP Pavilion CS از نسل چهارم این سری بسیار کمتر شده و وزن 1.9 کیلوگرمی آن، مزیتی به‌شمار می‌رود که جابه‌جایی این دستگاه را آسان می‌کند. صفحه‌نمایش این مدل، کیفیت Full HD با پنل‌ IPS WLED و روکش مات ضد انعکاس نور که به لطف فریم بسیار باریک اطراف LCD، لذت کار با این دستگاه را چند برابر میکند. پورت‌های ورودی و خروجی متعددی روی لبه‌های این لپ‌تاپ به چشم نمی خورند اما وجود پورت نسل جدید‌ USB type-C ، HDMI و پورت LAN شما را در کار های روزمره ی خود تنها نخواهند گذاشت. سخت‌افزار این محصول در بخش‌های پردازنده‌ی مرکزی و پردازشگر گرافیکی به‌روزرسانی شده و در پیکره‌بندی آن از جدیدترین مدل پردازنده‌های‌ اینتل ، یعنی نسل دهم آن و سری ‌MX250 پردازشگرهای‌ NVIDIA ‌استفاده شده است. مدل ‌‌Pavilion CS3457 به پردازنده‌ی مرکزی‌ Core i7 1065G7، پردازشگر گرافیکی‌ GeForce MX250با چهار گیگابایت حافظه‌ی اختصاصی، 16 گیگابایت رم ‌DDR4‌ و یک ترابایت حافظه‌ی داخلی به همراه 500 گیگابایت حافظه SSD M.2 که برای افزایش سرعت دستگاه تعبیه شده، مجهز شده، که به‌راحتی می‌تواند برنامه‌های نیمه‌حرفه‌ای و حرفه‌ای را اجرا کند. یک باتری سه‌سلولی لیتیوم‌یونی هم وظیفه‌ی توان‌رسانی به این دستگاه را دارد که در شرایط معمول وب‌گردی با اتصال‌ WiFi، حدود چهار ساعت روشن‌ماندن آن را تضمین می‌کند.                            &lt;/div&gt;', 3, 10, 0, 31200000, 6, 'on', 'دانیال رحیمی', 1600266578),
(5, 'لپ تاپ 15 اینچی لنوو مدل Ideapad L3 - NA', 'Lenovo Ideapad L3 - NA - 15 inch Laptop', '&lt;h2&gt;                        نقد و بررسی اجمالی                                                &lt;span&gt;Lenovo Ideapad L3 - NA - 15 inch Laptop&lt;/span&gt;&lt;/h2&gt;&lt;div&gt;                                لپ تاپ Ideapad L3 جزء سری های میان رده شرکت لنوو قرار دارد و در حال حاضر جدیدترین لپ تاپ 15 اینچ سری Ideapad این شرکت در بازار کشور محسوب می شود که در سال 2020 رونمایی گردید. این لپ تاپ با تلفیقی از دو مدل L340 و S540 طراحی گردیده و با سخت افزار مناسب و وزن سبک، مناسب امور روزمره، خانگی،گرافیکی نرمال، اداری و مهندسی با پردازش رو به بالا می باشد. ضخامت این دستگاه کمتر از 23mm و وزن دستگاه 2.2Kg می باشد که اعدادی متوسط برای یک لپ تاپ 15 اینچی میان رده محسوب می شوند و ازاین حیث گزینه نسبتا مناسبی برای کسانی که حمل و نقل زیادی با دستگاه خود دارند، می باشد.                            &lt;/div&gt;', 3, 333, 0, 4300000, 8, 'on', 'رسول بسامی', 1600266690),
